const { PrismaClient } = require('@prisma/client');
const bcrypt = require('bcryptjs');

const prisma = new PrismaClient();

async function main() {
    const adminEmail = 'admin@uvmarketschool.com';
    const existing = await prisma.user.findUnique({ where: { email: adminEmail } });

    if (!existing) {
        const passwordHash = await bcrypt.hash('admin123', 12);
        await prisma.user.create({
            data: {
                name: 'UV Market Admin',
                email: adminEmail,
                passwordHash,
                role: 'ADMIN',
                isActive: true,
                subscriptionStatus: 'active',
            },
        });
        console.log('Admin user created:');
        console.log('   Email: admin@uvmarketschool.com');
        console.log('   Password: admin123');
    } else {
        console.log('Admin user already exists.');
    }

    const admin = await prisma.user.findUnique({ where: { email: adminEmail } });
    if (!admin) return;

    const signalCount = await prisma.signal.count();
    if (signalCount === 0) {
        await prisma.signal.createMany({
            data: [
                {
                    createdById: admin.id,
                    symbol: 'TATASTEEL',
                    companyName: 'Tata Steel Ltd',
                    segment: 'Equity',
                    signalType: 'Swing',
                    direction: 'BUY',
                    entryPrice: 142.50,
                    targetPrice: 155.00,
                    stopLossPrice: 136.00,
                    status: 'TARGET_HIT',
                    exitPrice: 154.80,
                    returnPercent: 8.63,
                    notes: 'Strong breakout above resistance with volume confirmation.',
                    isVisibleToCustomers: true,
                },
                {
                    createdById: admin.id,
                    symbol: 'RELIANCE',
                    companyName: 'Reliance Industries Ltd',
                    segment: 'Equity',
                    signalType: 'Swing',
                    direction: 'BUY',
                    entryPrice: 2450.00,
                    targetPrice: 2600.00,
                    stopLossPrice: 2380.00,
                    status: 'ACTIVE',
                    notes: 'Bullish flag pattern forming on daily chart.',
                    isVisibleToCustomers: true,
                },
                {
                    createdById: admin.id,
                    symbol: 'HDFCBANK',
                    companyName: 'HDFC Bank Ltd',
                    segment: 'Equity',
                    signalType: 'Positional',
                    direction: 'BUY',
                    entryPrice: 1680.00,
                    targetPrice: 1780.00,
                    stopLossPrice: 1640.00,
                    status: 'TARGET_HIT',
                    exitPrice: 1775.00,
                    returnPercent: 5.65,
                    notes: 'Bounce from 200 DMA support.',
                    isVisibleToCustomers: true,
                },
                {
                    createdById: admin.id,
                    symbol: 'INFY',
                    companyName: 'Infosys Ltd',
                    segment: 'Equity',
                    signalType: 'Intraday',
                    direction: 'SELL',
                    entryPrice: 1520.00,
                    targetPrice: 1480.00,
                    stopLossPrice: 1540.00,
                    status: 'STOP_LOSS',
                    exitPrice: 1540.00,
                    returnPercent: -1.32,
                    notes: 'Bearish engulfing candle on hourly chart.',
                    isVisibleToCustomers: true,
                },
                {
                    createdById: admin.id,
                    symbol: 'SBIN',
                    companyName: 'State Bank of India',
                    segment: 'Equity',
                    signalType: 'Swing',
                    direction: 'BUY',
                    entryPrice: 780.00,
                    targetPrice: 830.00,
                    stopLossPrice: 755.00,
                    status: 'PENDING',
                    notes: 'Awaiting breakout confirmation above 785 level.',
                    isVisibleToCustomers: true,
                },
                {
                    createdById: admin.id,
                    symbol: 'TCS',
                    companyName: 'Tata Consultancy Services',
                    segment: 'Equity',
                    signalType: 'Positional',
                    direction: 'BUY',
                    entryPrice: 3950.00,
                    targetPrice: 4150.00,
                    stopLossPrice: 3850.00,
                    status: 'TARGET_HIT',
                    exitPrice: 4140.00,
                    returnPercent: 4.81,
                    notes: 'Strong quarterly results catalyst.',
                    isVisibleToCustomers: true,
                },
            ],
        });
        console.log('Sample signals created (6 signals).');
    } else {
        console.log('Signals already exist, skipping seed.');
    }
}

main()
    .catch((e) => {
        console.error(e);
        process.exit(1);
    })
    .finally(() => prisma.$disconnect());

'use client';

import { useState, useEffect } from 'react';
import { FileText, Download, Search, Plus, Loader2 } from 'lucide-react';

interface Invoice {
    id: string;
    invoiceNumber: string;
    userId: string;
    customerName: string;
    customerEmail: string;
    subtotal: number;
    gstPercent: number;
    gstAmount: number;
    totalAmount: number;
    companyName: string;
    companyGST: string | null;
    createdAt: string;
    payment: { planName: string; razorpayPaymentId: string | null };
}

export default function AdminInvoicesPage() {
    const [invoices, setInvoices] = useState<Invoice[]>([]);
    const [loading, setLoading] = useState(true);
    const [search, setSearch] = useState('');

    useEffect(() => {
        fetch('/api/invoices')
            .then(r => r.json())
            .then(d => { setInvoices(d.invoices || []); setLoading(false); })
            .catch(() => setLoading(false));
    }, []);

    const exportCSV = () => {
        window.open('/api/export?type=invoices&format=csv', '_blank');
    };

    const filtered = invoices.filter(i =>
        i.customerName.toLowerCase().includes(search.toLowerCase()) ||
        i.invoiceNumber.toLowerCase().includes(search.toLowerCase()) ||
        i.customerEmail.toLowerCase().includes(search.toLowerCase())
    );

    if (loading) return <div className="p-8 text-center text-gray-500">Loading invoices...</div>;

    return (
        <div className="max-w-6xl mx-auto px-4 py-8 space-y-6">
            <div className="flex items-center justify-between">
                <div>
                    <h1 className="text-2xl font-bold text-gray-800 flex items-center gap-3">
                        <FileText className="text-purple-600" size={24} />
                        Invoices
                    </h1>
                    <p className="text-gray-500 text-sm mt-1">{invoices.length} total invoices</p>
                </div>
                <button onClick={exportCSV} className="flex items-center gap-2 px-4 py-2.5 bg-green-600 text-white rounded-xl text-sm font-semibold hover:bg-green-700 transition-all shadow-lg">
                    <Download size={16} /> Export CSV
                </button>
            </div>

            {/* Search */}
            <div className="relative">
                <input
                    type="text"
                    placeholder="Search by name, email, or invoice number..."
                    value={search}
                    onChange={e => setSearch(e.target.value)}
                    className="w-full pl-11 pr-4 py-3 rounded-xl border border-gray-200 bg-white shadow-sm focus:ring-2 focus:ring-purple-500/20 focus:border-purple-500 outline-none"
                />
                <Search className="absolute left-4 top-1/2 -translate-y-1/2 text-gray-400" size={18} />
            </div>

            {/* Table */}
            <div className="bg-white rounded-2xl shadow-sm border border-gray-100 overflow-hidden">
                <div className="overflow-x-auto">
                    <table className="w-full text-sm">
                        <thead className="bg-gray-50">
                            <tr>
                                <th className="px-4 py-3 text-left text-xs font-bold text-gray-500 uppercase">Invoice #</th>
                                <th className="px-4 py-3 text-left text-xs font-bold text-gray-500 uppercase">Date</th>
                                <th className="px-4 py-3 text-left text-xs font-bold text-gray-500 uppercase">Customer</th>
                                <th className="px-4 py-3 text-left text-xs font-bold text-gray-500 uppercase">Plan</th>
                                <th className="px-4 py-3 text-right text-xs font-bold text-gray-500 uppercase">Subtotal</th>
                                <th className="px-4 py-3 text-right text-xs font-bold text-gray-500 uppercase">GST</th>
                                <th className="px-4 py-3 text-right text-xs font-bold text-gray-500 uppercase">Total</th>
                            </tr>
                        </thead>
                        <tbody className="divide-y divide-gray-50">
                            {filtered.map(inv => (
                                <tr key={inv.id} className="hover:bg-gray-50/50 transition-colors">
                                    <td className="px-4 py-3 font-mono text-purple-600 font-semibold">{inv.invoiceNumber}</td>
                                    <td className="px-4 py-3 text-gray-500">
                                        {new Date(inv.createdAt).toLocaleDateString('en-IN', { day: 'numeric', month: 'short', year: 'numeric' })}
                                    </td>
                                    <td className="px-4 py-3">
                                        <div className="font-medium text-gray-800">{inv.customerName}</div>
                                        <div className="text-xs text-gray-400">{inv.customerEmail}</div>
                                    </td>
                                    <td className="px-4 py-3">
                                        <span className="px-2 py-0.5 bg-blue-50 text-blue-700 rounded text-xs font-semibold">{inv.payment?.planName}</span>
                                    </td>
                                    <td className="px-4 py-3 text-right text-gray-600">₹{inv.subtotal.toFixed(2)}</td>
                                    <td className="px-4 py-3 text-right text-gray-400">₹{inv.gstAmount.toFixed(2)} ({inv.gstPercent}%)</td>
                                    <td className="px-4 py-3 text-right font-bold text-gray-800">₹{inv.totalAmount.toFixed(2)}</td>
                                </tr>
                            ))}
                        </tbody>
                    </table>
                </div>
                {filtered.length === 0 && (
                    <div className="py-12 text-center text-gray-400">
                        <FileText size={32} className="mx-auto mb-2 text-gray-300" />
                        <p>No invoices found.</p>
                    </div>
                )}
            </div>
        </div>
    );
}

'use client';

import { useEffect, useState, useCallback } from 'react';
import { LoadingSpinner } from '@/components/Loading';
import AdminCharts from '@/components/admin/AdminCharts';
import AIDataAnalysis from '@/components/AIDataAnalysis';
import { Users, CreditCard, TrendingUp, Target, Percent, Activity, Sparkles, ArrowUpRight, BarChart3 } from 'lucide-react';

interface ChartData {
    revenue: { name: string; value: number }[];
    performance: { name: string; value: number }[];
}

interface AdminStats {
    totalCustomers: number;
    activeSubscriptions: number;
    totalSignals: number;
    totalClosed: number;
    winners: number;
    winRate: number;
    avgReturn: number;
    totalRevenue: number;
    totalPayments: number;
    charts?: ChartData;
}

export default function AdminDashboard() {
    const [stats, setStats] = useState<AdminStats | null>(null);
    const [signals, setSignals] = useState<any[]>([]);
    const [loading, setLoading] = useState(true);

    const fetchStats = useCallback(async () => {
        try {
            const [statsRes, signalsRes] = await Promise.all([
                fetch('/api/admin/stats'),
                fetch('/api/signals?status=closed&limit=500'),
            ]);
            const [statsData, signalsData] = await Promise.all([
                statsRes.json(),
                signalsRes.json(),
            ]);
            setStats(statsData);
            setSignals(signalsData.signals || []);
        } catch (err) {
            console.error('Error fetching admin stats:', err);
        } finally {
            setLoading(false);
        }
    }, []);

    useEffect(() => {
        fetchStats();
    }, [fetchStats]);

    if (loading) return <LoadingSpinner />;

    return (
        <div className="relative">
            {/* Decorative elements */}
            <div className="absolute top-0 right-0 w-[400px] h-[400px] rounded-full opacity-30 pointer-events-none"
                style={{ background: 'radial-gradient(circle, rgba(139,92,246,0.06) 0%, transparent 70%)' }} />

            {/* Header - Bigger */}
            <div className="flex items-center justify-between mb-10">
                <div>
                    <div className="flex items-center gap-3">
                        <div className="p-2.5 bg-gradient-to-br from-purple-500 to-indigo-600 rounded-xl shadow-lg shadow-purple-500/20">
                            <BarChart3 size={22} className="text-white" />
                        </div>
                        <div>
                            <h1 className="text-3xl font-extrabold text-gray-800 tracking-tight">Admin Dashboard</h1>
                            <p className="text-gray-500 text-sm mt-1">Overview of your signals and business performance</p>
                        </div>
                    </div>
                </div>
                <div className="flex items-center gap-3">
                    <div className="flex items-center gap-2 text-sm text-gray-400 bg-white/80 backdrop-blur-sm px-4 py-2.5 rounded-xl border border-gray-100 shadow-sm">
                        <Activity size={14} className="text-green-500 animate-pulse" />
                        Live
                    </div>
                    <div className="text-sm text-gray-400 bg-white/80 backdrop-blur-sm px-4 py-2.5 rounded-xl border border-gray-100 shadow-sm">
                        Last updated: {new Date().toLocaleTimeString()}
                    </div>
                </div>
            </div>

            {/* Top Stats Row - Bigger */}
            <div className="grid grid-cols-1 md:grid-cols-2 xl:grid-cols-4 gap-6">
                {/* Customers */}
                <div className="group bg-white/80 backdrop-blur-sm p-7 rounded-2xl shadow-sm border border-gray-100/80 transition-all duration-300 hover:shadow-xl hover:-translate-y-1 relative overflow-hidden">
                    <div className="absolute top-0 right-0 w-24 h-24 bg-gradient-to-bl from-blue-100/50 to-transparent rounded-bl-full" />
                    <div className="flex items-center gap-3 mb-4">
                        <div className="p-3 bg-gradient-to-br from-blue-50 to-blue-100 rounded-xl text-blue-600 shadow-sm">
                            <Users size={22} />
                        </div>
                        <span className="text-sm font-bold text-gray-500 uppercase tracking-wider">Customers</span>
                    </div>
                    <div className="text-4xl font-extrabold text-gray-800 tracking-tight">{stats?.totalCustomers || 0}</div>
                    <div className="text-xs text-green-600 mt-3 font-semibold flex items-center gap-1.5">
                        <span className="bg-green-50 px-3 py-1 rounded-full flex items-center gap-1">
                            <ArrowUpRight size={10} />
                            {stats?.activeSubscriptions || 0} Active
                        </span>
                    </div>
                </div>

                {/* Revenue */}
                <div className="group bg-white/80 backdrop-blur-sm p-7 rounded-2xl shadow-sm border border-gray-100/80 transition-all duration-300 hover:shadow-xl hover:-translate-y-1 relative overflow-hidden">
                    <div className="absolute top-0 right-0 w-24 h-24 bg-gradient-to-bl from-purple-100/50 to-transparent rounded-bl-full" />
                    <div className="flex items-center gap-3 mb-4">
                        <div className="p-3 bg-gradient-to-br from-purple-50 to-purple-100 rounded-xl text-purple-600 shadow-sm">
                            <CreditCard size={22} />
                        </div>
                        <span className="text-sm font-bold text-gray-500 uppercase tracking-wider">Revenue</span>
                    </div>
                    <div className="text-4xl font-extrabold text-gray-800 tracking-tight">
                        ₹{(stats?.totalRevenue || 0).toLocaleString('en-IN')}
                    </div>
                    <div className="text-xs text-gray-400 mt-3 font-medium">
                        Total payments: {stats?.totalPayments || 0}
                    </div>
                </div>

                {/* Signals */}
                <div className="group bg-white/80 backdrop-blur-sm p-7 rounded-2xl shadow-sm border border-gray-100/80 transition-all duration-300 hover:shadow-xl hover:-translate-y-1 relative overflow-hidden">
                    <div className="absolute top-0 right-0 w-24 h-24 bg-gradient-to-bl from-amber-100/50 to-transparent rounded-bl-full" />
                    <div className="flex items-center gap-3 mb-4">
                        <div className="p-3 bg-gradient-to-br from-amber-50 to-amber-100 rounded-xl text-amber-600 shadow-sm">
                            <Target size={22} />
                        </div>
                        <span className="text-sm font-bold text-gray-500 uppercase tracking-wider">Signals</span>
                    </div>
                    <div className="text-4xl font-extrabold text-gray-800 tracking-tight">{stats?.totalSignals || 0}</div>
                    <div className="text-xs text-gray-400 mt-3 font-medium">
                        {stats?.totalClosed || 0} Closed
                    </div>
                </div>

                {/* Win Rate */}
                <div className="group bg-white/80 backdrop-blur-sm p-7 rounded-2xl shadow-sm border border-gray-100/80 transition-all duration-300 hover:shadow-xl hover:-translate-y-1 relative overflow-hidden">
                    <div className="absolute top-0 right-0 w-24 h-24 bg-gradient-to-bl from-green-100/50 to-transparent rounded-bl-full" />
                    <div className="flex items-center gap-3 mb-4">
                        <div className="p-3 bg-gradient-to-br from-green-50 to-green-100 rounded-xl text-green-600 shadow-sm">
                            <TrendingUp size={22} />
                        </div>
                        <span className="text-sm font-bold text-gray-500 uppercase tracking-wider">Win Rate</span>
                    </div>
                    <div className={`text-4xl font-extrabold tracking-tight ${(stats?.winRate || 0) >= 50 ? 'text-green-600' : 'text-red-500'}`}>
                        {stats?.winRate || 0}%
                    </div>
                    <div className="text-xs text-gray-400 mt-3 font-medium flex items-center gap-1.5">
                        <Sparkles size={10} className="text-amber-500" />
                        {stats?.winners || 0} Winning Trades
                    </div>
                </div>
            </div>

            {/* Charts Section */}
            {stats?.charts && <AdminCharts data={stats.charts} />}

            {/* Secondary Stats Row - Bigger */}
            <div className="grid grid-cols-1 md:grid-cols-2 xl:grid-cols-4 gap-6 mt-8">
                <div className="bg-white/80 backdrop-blur-sm p-6 rounded-2xl border border-gray-100/80 flex items-center justify-between shadow-sm hover:shadow-md transition-all">
                    <div>
                        <div className="text-xs text-gray-500 uppercase font-bold tracking-wider">Avg Return</div>
                        <div className={`text-2xl font-extrabold mt-2 ${(stats?.avgReturn || 0) >= 0 ? 'text-green-600' : 'text-red-500'}`}>
                            {stats?.avgReturn || 0}%
                        </div>
                    </div>
                    <div className="p-3 bg-gray-50 rounded-full text-gray-400">
                        <Percent size={20} />
                    </div>
                </div>
                <div className="bg-white/80 backdrop-blur-sm p-6 rounded-2xl border border-gray-100/80 flex items-center justify-between shadow-sm hover:shadow-md transition-all">
                    <div>
                        <div className="text-xs text-gray-500 uppercase font-bold tracking-wider">Active Subs</div>
                        <div className="text-2xl font-extrabold mt-2 text-blue-600">
                            {stats?.activeSubscriptions || 0}
                        </div>
                    </div>
                    <div className="p-3 bg-blue-50 rounded-full text-blue-400">
                        <Users size={20} />
                    </div>
                </div>
                <div className="bg-white/80 backdrop-blur-sm p-6 rounded-2xl border border-gray-100/80 flex items-center justify-between shadow-sm hover:shadow-md transition-all">
                    <div>
                        <div className="text-xs text-gray-500 uppercase font-bold tracking-wider">Winners</div>
                        <div className="text-2xl font-extrabold mt-2 text-green-600">
                            {stats?.winners || 0}
                        </div>
                    </div>
                    <div className="p-3 bg-green-50 rounded-full text-green-400">
                        <TrendingUp size={20} />
                    </div>
                </div>
                <div className="bg-white/80 backdrop-blur-sm p-6 rounded-2xl border border-gray-100/80 flex items-center justify-between shadow-sm hover:shadow-md transition-all">
                    <div>
                        <div className="text-xs text-gray-500 uppercase font-bold tracking-wider">Total Closed</div>
                        <div className="text-2xl font-extrabold mt-2 text-gray-800">
                            {stats?.totalClosed || 0}
                        </div>
                    </div>
                    <div className="p-3 bg-gray-50 rounded-full text-gray-400">
                        <Target size={20} />
                    </div>
                </div>
            </div>

            {/* AI Data Analysis Section */}
            <AIDataAnalysis signals={signals} title="AI Data Analysis" />
        </div>
    );
}

'use client';

import { useState, useEffect } from 'react';
import { Plus, Edit2, Trash2, Save, X, Loader2, Package } from 'lucide-react';

interface Plan {
    id: string;
    name: string;
    description: string | null;
    price: number;
    duration: number;
    isActive: boolean;
    features: string | null;
    sortOrder: number;
}

export default function AdminPlansPage() {
    const [plans, setPlans] = useState<Plan[]>([]);
    const [loading, setLoading] = useState(true);
    const [editing, setEditing] = useState<Plan | null>(null);
    const [isNew, setIsNew] = useState(false);
    const [saving, setSaving] = useState(false);

    useEffect(() => {
        fetchPlans();
    }, []);

    const fetchPlans = async () => {
        try {
            const res = await fetch('/api/admin/plans');
            const data = await res.json();
            setPlans(data.plans || []);
        } catch { } finally {
            setLoading(false);
        }
    };

    const handleSave = async () => {
        if (!editing) return;
        setSaving(true);
        try {
            const method = isNew ? 'POST' : 'PUT';
            const body = isNew ? {
                name: editing.name,
                description: editing.description,
                price: editing.price,
                duration: editing.duration,
                features: editing.features ? JSON.parse(editing.features) : [],
                sortOrder: editing.sortOrder,
            } : editing;

            const res = await fetch('/api/admin/plans', {
                method,
                headers: { 'Content-Type': 'application/json' },
                body: JSON.stringify(body),
            });

            if (res.ok) {
                fetchPlans();
                setEditing(null);
                setIsNew(false);
            }
        } catch { } finally {
            setSaving(false);
        }
    };

    const handleDelete = async (id: string) => {
        if (!confirm('Delete this plan?')) return;
        await fetch(`/api/admin/plans?id=${id}`, { method: 'DELETE' });
        fetchPlans();
    };

    const startNew = () => {
        setEditing({
            id: '',
            name: '',
            description: '',
            price: 0,
            duration: 30,
            isActive: true,
            features: '["Feature 1", "Feature 2"]',
            sortOrder: plans.length,
        });
        setIsNew(true);
    };

    if (loading) return <div className="p-8 text-center text-gray-500">Loading plans...</div>;

    return (
        <div className="max-w-5xl mx-auto px-4 py-8 space-y-6">
            <div className="flex items-center justify-between">
                <div>
                    <h1 className="text-2xl font-bold text-gray-800 flex items-center gap-3">
                        <Package className="text-purple-600" size={24} />
                        Subscription Plans
                    </h1>
                    <p className="text-gray-500 text-sm mt-1">Manage pricing plans visible on the pricing page</p>
                </div>
                <button onClick={startNew} className="flex items-center gap-2 px-5 py-2.5 bg-purple-600 text-white rounded-xl font-semibold hover:bg-purple-700 transition-all shadow-lg">
                    <Plus size={16} /> Add Plan
                </button>
            </div>

            {/* Plans Grid */}
            <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
                {plans.map(plan => (
                    <div key={plan.id} className={`bg-white rounded-2xl shadow-sm border p-6 relative ${plan.isActive ? 'border-gray-100' : 'border-red-200 opacity-60'}`}>
                        {!plan.isActive && (
                            <span className="absolute top-3 right-3 text-[10px] bg-red-100 text-red-600 px-2 py-0.5 rounded-full font-bold">INACTIVE</span>
                        )}
                        <h3 className="text-lg font-bold text-gray-800">{plan.name}</h3>
                        <p className="text-gray-500 text-sm mt-1">{plan.description || 'No description'}</p>
                        <div className="mt-4">
                            <span className="text-3xl font-bold text-gray-800">₹{plan.price}</span>
                            <span className="text-gray-400 text-sm"> / {plan.duration} days</span>
                        </div>
                        {plan.features && (
                            <ul className="mt-4 space-y-1.5 text-sm text-gray-600">
                                {JSON.parse(plan.features).map((f: string, i: number) => (
                                    <li key={i} className="flex items-center gap-2">
                                        <span className="w-1.5 h-1.5 rounded-full bg-green-500"></span>
                                        {f}
                                    </li>
                                ))}
                            </ul>
                        )}
                        <div className="flex gap-2 mt-6">
                            <button onClick={() => { setEditing(plan); setIsNew(false); }}
                                className="flex-1 flex items-center justify-center gap-1.5 px-3 py-2 bg-gray-50 text-gray-700 rounded-lg text-sm font-medium hover:bg-gray-100 transition-colors">
                                <Edit2 size={14} /> Edit
                            </button>
                            <button onClick={() => handleDelete(plan.id)}
                                className="flex items-center justify-center gap-1.5 px-3 py-2 bg-red-50 text-red-600 rounded-lg text-sm font-medium hover:bg-red-100 transition-colors">
                                <Trash2 size={14} />
                            </button>
                        </div>
                    </div>
                ))}
            </div>

            {plans.length === 0 && !editing && (
                <div className="py-12 text-center bg-white rounded-2xl border border-dashed border-gray-300">
                    <Package size={32} className="mx-auto mb-3 text-gray-300" />
                    <h3 className="text-gray-900 font-medium">No plans created yet</h3>
