    } catch (error) {
        console.error('Error deleting signal:', error);
        return NextResponse.json({ error: 'Failed to delete signal' }, { status: 500 });
    }
}

import { NextRequest, NextResponse } from 'next/server';
import { getServerSession } from 'next-auth';
import { authOptions } from '@/lib/auth';
import { prisma } from '@/lib/prisma';

// GET /api/signals — public (limited) or authenticated (full)
export async function GET(req: NextRequest) {
    try {
        const session = await getServerSession(authOptions);
        const { searchParams } = new URL(req.url);
        const status = searchParams.get('status');
        const limit = parseInt(searchParams.get('limit') || '50');
        const page = parseInt(searchParams.get('page') || '1');
        const skip = (page - 1) * limit;

        // Build where clause
        const where: any = {};

        if (!session || (session.user as any).role !== 'ADMIN') {
            where.isVisibleToCustomers = true;
        }

        if (status) {
            if (status === 'active') {
                where.status = { in: ['PENDING', 'ACTIVE'] };
            } else if (status === 'closed') {
                where.status = { in: ['TARGET_HIT', 'STOP_LOSS', 'CLOSED_MANUAL'] };
            } else {
                where.status = status;
            }
        }

        const [signals, total] = await Promise.all([
            prisma.signal.findMany({
                where,
                orderBy: { createdAt: 'desc' },
                take: limit,
                skip,
                include: {
                    screenshots: true,
                    createdBy: {
                        select: { name: true },
                    },
                },
            }),
            prisma.signal.count({ where }),
        ]);

        return NextResponse.json({ signals, total, page, limit });
    } catch (error) {
        console.error('Error fetching signals:', error);
        return NextResponse.json({ error: 'Failed to fetch signals' }, { status: 500 });
    }
}

// POST /api/signals — admin only
export async function POST(req: NextRequest) {
    try {
        const session = await getServerSession(authOptions);
        if (!session || (session.user as any).role !== 'ADMIN') {
            return NextResponse.json({ error: 'Unauthorized' }, { status: 401 });
        }

        const body = await req.json();
        const {
            symbol,
            companyName,
            segment,
            signalType,
            direction,
            entryPrice,
            targetPrice,
            stopLossPrice,
            notes,
            isVisibleToCustomers,
            status,
        } = body;

        if (!symbol || !companyName || !direction || !entryPrice || !targetPrice || !stopLossPrice) {
            return NextResponse.json({ error: 'Missing required fields' }, { status: 400 });
        }

        const signal = await prisma.signal.create({
            data: {
                createdById: (session.user as any).id,
                symbol: symbol.toUpperCase(),
                companyName,
                segment: segment || 'Equity',
                signalType: signalType || 'Swing',
                direction,
                entryPrice: parseFloat(entryPrice),
                targetPrice: parseFloat(targetPrice),
                stopLossPrice: parseFloat(stopLossPrice),
                notes: notes || null,
                isVisibleToCustomers: isVisibleToCustomers !== false,
                status: status || 'ACTIVE',
            },
        });

        return NextResponse.json({ signal }, { status: 201 });
    } catch (error) {
        console.error('Error creating signal:', error);
        return NextResponse.json({ error: 'Failed to create signal' }, { status: 500 });
    }
}

'use client';

import { useState, useEffect, useCallback } from 'react';
import { Signal } from '@/types/dashboard';
import { Calculator, TrendingUp, IndianRupee, BarChart3, PiggyBank } from 'lucide-react';
import { useSession } from 'next-auth/react';
import { useRouter } from 'next/navigation';
import { LoadingSpinner } from '@/components/Loading';

export default function ProfitCalculatorPage() {
    const { data: session, status } = useSession();
    const router = useRouter();
    const [closedSignals, setClosedSignals] = useState<Signal[]>([]);
    const [loading, setLoading] = useState(true);
    const [capital, setCapital] = useState(100000);
    const [perTradePercent, setPerTradePercent] = useState(10);

    useEffect(() => {
        if (status === 'loading') return;
        if (!session) { router.push('/login'); return; }
        fetchData();
    }, [session, status, router]);

    const fetchData = async () => {
        try {
            const res = await fetch('/api/signals?status=closed&limit=500');
            const data = await res.json();
            setClosedSignals(data.signals || []);
        } catch { } finally {
            setLoading(false);
        }
    };

    if (status === 'loading' || loading) return <LoadingSpinner />;

    // Calculate
    const perTradeCapital = (capital * perTradePercent) / 100;
    let runningCapital = capital;
    const tradeLog: { symbol: string; returnPct: number; pnl: number; cumCapital: number; date: string }[] = [];

    closedSignals
        .sort((a, b) => new Date(a.entryDateTime).getTime() - new Date(b.entryDateTime).getTime())
        .forEach(s => {
            if (s.returnPercent !== null) {
                const invested = (runningCapital * perTradePercent) / 100;
                const pnl = (invested * s.returnPercent) / 100;
                runningCapital += pnl;
                tradeLog.push({
                    symbol: s.symbol,
                    returnPct: s.returnPercent,
                    pnl: Math.round(pnl),
                    cumCapital: Math.round(runningCapital),
                    date: new Date(s.entryDateTime).toLocaleDateString('en-IN'),
                });
            }
        });

    const totalPnL = runningCapital - capital;
    const totalReturnPct = ((runningCapital - capital) / capital) * 100;
    const winners = tradeLog.filter(t => t.pnl > 0).length;
    const losers = tradeLog.filter(t => t.pnl <= 0).length;

    return (
        <div className="min-h-screen bg-gray-50/50 pb-20">
            <div className="max-w-6xl mx-auto px-4 md:px-6 py-8">
                <div className="mb-8">
                    <h1 className="text-3xl font-bold text-gray-800 flex items-center gap-3">
                        <Calculator className="text-purple-600" size={28} />
                        Profit Calculator
                    </h1>
                    <p className="text-gray-500 mt-1">See what you could have earned by following all signals</p>
                </div>

                {/* Capital Input */}
                <div className="bg-white rounded-2xl shadow-sm border border-gray-100 p-6 mb-8">
                    <h2 className="font-bold text-gray-800 mb-4">Configure Your Portfolio</h2>
                    <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
                        <div>
                            <label className="block text-sm font-semibold text-gray-600 mb-2">Starting Capital (₹)</label>
                            <input
                                type="number"
                                value={capital}
                                onChange={e => setCapital(Math.max(0, parseInt(e.target.value) || 0))}
                                className="w-full px-4 py-3 rounded-xl border border-gray-200 text-lg font-semibold focus:ring-2 focus:ring-purple-500/20 focus:border-purple-500 outline-none"
                            />
                            <div className="flex gap-2 mt-2">
                                {[50000, 100000, 200000, 500000].map(v => (
                                    <button key={v} onClick={() => setCapital(v)}
                                        className={`text-xs px-3 py-1 rounded-full font-medium transition-colors ${capital === v ? 'bg-purple-600 text-white' : 'bg-gray-100 text-gray-600 hover:bg-gray-200'}`}>
                                        ₹{(v / 1000)}K
                                    </button>
                                ))}
                            </div>
                        </div>
                        <div>
                            <label className="block text-sm font-semibold text-gray-600 mb-2">Per Trade Allocation (%)</label>
                            <input
                                type="range"
                                min={1} max={100} value={perTradePercent}
                                onChange={e => setPerTradePercent(parseInt(e.target.value))}
                                className="w-full accent-purple-600"
                            />
                            <div className="flex justify-between text-sm text-gray-500 mt-1">
                                <span>{perTradePercent}% of capital</span>
                                <span className="font-semibold text-gray-800">₹{Math.round(perTradeCapital).toLocaleString('en-IN')}</span>
                            </div>
                        </div>
                    </div>
                </div>

                {/* Summary Stats */}
                <div className="grid grid-cols-2 md:grid-cols-4 gap-4 mb-8">
                    <div className="bg-white p-5 rounded-2xl shadow-sm border border-gray-100 text-center">
                        <PiggyBank className="mx-auto text-blue-500 mb-2" size={24} />
                        <div className="text-xs text-gray-500 uppercase font-semibold mb-1">Starting</div>
                        <div className="text-xl font-bold text-gray-800">₹{capital.toLocaleString('en-IN')}</div>
                    </div>
                    <div className="bg-white p-5 rounded-2xl shadow-sm border border-gray-100 text-center">
                        <IndianRupee className="mx-auto text-green-500 mb-2" size={24} />
                        <div className="text-xs text-gray-500 uppercase font-semibold mb-1">Final Capital</div>
                        <div className="text-xl font-bold text-green-700">₹{runningCapital.toLocaleString('en-IN')}</div>
                    </div>
                    <div className="bg-white p-5 rounded-2xl shadow-sm border border-gray-100 text-center">
                        <TrendingUp className="mx-auto text-purple-500 mb-2" size={24} />
                        <div className="text-xs text-gray-500 uppercase font-semibold mb-1">Total PnL</div>
                        <div className={`text-xl font-bold ${totalPnL >= 0 ? 'text-green-600' : 'text-red-600'}`}>
                            {totalPnL >= 0 ? '+' : ''}₹{Math.round(totalPnL).toLocaleString('en-IN')}
                        </div>
                    </div>
                    <div className="bg-white p-5 rounded-2xl shadow-sm border border-gray-100 text-center">
                        <BarChart3 className="mx-auto text-amber-500 mb-2" size={24} />
                        <div className="text-xs text-gray-500 uppercase font-semibold mb-1">Total Return</div>
                        <div className={`text-xl font-bold ${totalReturnPct >= 0 ? 'text-green-600' : 'text-red-600'}`}>
                            {totalReturnPct >= 0 ? '+' : ''}{totalReturnPct.toFixed(2)}%
                        </div>
                    </div>
                </div>

                {/* Trade Log Table */}
                <div className="bg-white rounded-2xl shadow-sm border border-gray-100 overflow-hidden">
                    <div className="p-4 border-b border-gray-100 flex items-center justify-between">
                        <h3 className="font-bold text-gray-800">Trade-by-Trade Breakdown</h3>
                        <span className="text-xs text-gray-400">{tradeLog.length} trades • {winners} wins • {losers} losses</span>
                    </div>
                    <div className="overflow-x-auto max-h-[400px] overflow-y-auto">
                        <table className="w-full text-sm">
                            <thead className="bg-gray-50 sticky top-0">
                                <tr>
                                    <th className="px-4 py-3 text-left text-xs font-bold text-gray-500 uppercase">#</th>
                                    <th className="px-4 py-3 text-left text-xs font-bold text-gray-500 uppercase">Date</th>
                                    <th className="px-4 py-3 text-left text-xs font-bold text-gray-500 uppercase">Symbol</th>
                                    <th className="px-4 py-3 text-right text-xs font-bold text-gray-500 uppercase">Return %</th>
                                    <th className="px-4 py-3 text-right text-xs font-bold text-gray-500 uppercase">PnL (₹)</th>
                                    <th className="px-4 py-3 text-right text-xs font-bold text-gray-500 uppercase">Capital</th>
                                </tr>
                            </thead>
                            <tbody className="divide-y divide-gray-50">
                                {tradeLog.map((t, i) => (
                                    <tr key={i} className="hover:bg-gray-50/50 transition-colors">
                                        <td className="px-4 py-2.5 text-gray-400 font-mono text-xs">{i + 1}</td>
                                        <td className="px-4 py-2.5 text-gray-600">{t.date}</td>
                                        <td className="px-4 py-2.5 font-semibold text-gray-800">{t.symbol}</td>
                                        <td className={`px-4 py-2.5 text-right font-semibold ${t.returnPct > 0 ? 'text-green-600' : 'text-red-600'}`}>
                                            {t.returnPct > 0 ? '+' : ''}{t.returnPct.toFixed(2)}%
                                        </td>
                                        <td className={`px-4 py-2.5 text-right font-semibold ${t.pnl > 0 ? 'text-green-600' : 'text-red-600'}`}>
                                            {t.pnl > 0 ? '+' : ''}₹{t.pnl.toLocaleString('en-IN')}
                                        </td>
                                        <td className="px-4 py-2.5 text-right text-gray-800 font-medium">₹{t.cumCapital.toLocaleString('en-IN')}</td>
                                    </tr>
                                ))}
                            </tbody>
                        </table>
                    </div>
                    {tradeLog.length === 0 && (
                        <div className="py-12 text-center text-gray-400">
                            <Calculator size={32} className="mx-auto mb-2 text-gray-300" />
                            <p>No closed trades to calculate. Results will appear as signals close.</p>
                        </div>
                    )}
                </div>

                <div className="mt-6 text-center text-xs text-gray-400">
                    ⚠️ This calculator is for educational purposes only. Past performance does not guarantee future results.
                    All calculations are hypothetical and assume perfect execution.
                </div>
            </div>
        </div>
    );
}

