                />
            )}
        </div>
    );
}

'use client';

import { useState } from 'react';
import Link from 'next/link';
import { Mail, ArrowLeft, Check, Loader2 } from 'lucide-react';

export default function ForgotPasswordPage() {
    const [email, setEmail] = useState('');
    const [loading, setLoading] = useState(false);
    const [sent, setSent] = useState(false);
    const [error, setError] = useState('');

    const handleSubmit = async (e: React.FormEvent) => {
        e.preventDefault();
        setLoading(true);
        setError('');
        try {
            const res = await fetch('/api/auth/forgot-password', {
                method: 'POST',
                headers: { 'Content-Type': 'application/json' },
                body: JSON.stringify({ email }),
            });
            const data = await res.json();
            if (res.ok) setSent(true);
            else setError(data.error || 'Something went wrong');
        } catch {
            setError('Network error. Try again.');
        } finally {
            setLoading(false);
        }
    };

    return (
        <div className="min-h-screen flex items-center justify-center bg-gray-50 px-4">
            <div className="w-full max-w-md">
                <div className="bg-white rounded-2xl shadow-xl p-8 border border-gray-100">
                    {!sent ? (
                        <>
                            <div className="text-center mb-8">
                                <div className="inline-flex p-3 bg-purple-50 rounded-2xl mb-4">
                                    <Mail className="text-purple-600" size={28} />
                                </div>
                                <h1 className="text-2xl font-bold text-gray-800">Forgot Password?</h1>
                                <p className="text-gray-500 mt-2 text-sm">
                                    Enter your email and we'll send you a reset link.
                                </p>
                            </div>

                            {error && (
                                <div className="bg-red-50 text-red-700 px-4 py-3 rounded-lg text-sm mb-4 border border-red-100">
                                    {error}
                                </div>
                            )}

                            <form onSubmit={handleSubmit} className="space-y-5">
                                <div>
                                    <label className="block text-sm font-semibold text-gray-700 mb-2">Email Address</label>
                                    <input
                                        type="email"
                                        required
                                        value={email}
                                        onChange={(e) => setEmail(e.target.value)}
                                        placeholder="you@example.com"
                                        className="w-full px-4 py-3 rounded-xl border border-gray-200 focus:ring-2 focus:ring-purple-500/20 focus:border-purple-500 outline-none transition-all"
                                    />
                                </div>
                                <button
                                    type="submit"
                                    disabled={loading}
                                    className="w-full py-3 bg-purple-600 hover:bg-purple-700 text-white font-bold rounded-xl transition-all shadow-lg hover:shadow-xl flex items-center justify-center gap-2 disabled:opacity-60"
                                >
                                    {loading ? <Loader2 className="animate-spin" size={18} /> : null}
                                    {loading ? 'Sending...' : 'Send Reset Link'}
                                </button>
                            </form>
                        </>
                    ) : (
                        <div className="text-center py-4">
                            <div className="inline-flex p-4 bg-green-50 rounded-full mb-4">
                                <Check className="text-green-600" size={32} />
                            </div>
                            <h2 className="text-xl font-bold text-gray-800 mb-2">Check Your Email</h2>
                            <p className="text-gray-500 text-sm">
                                If an account exists with <strong>{email}</strong>, we've sent a password reset link.
                            </p>
                        </div>
                    )}

                    <div className="mt-6 text-center">
                        <Link href="/login" className="text-sm text-purple-600 hover:text-purple-800 font-medium flex items-center justify-center gap-1">
                            <ArrowLeft size={14} />
                            Back to Login
                        </Link>
                    </div>
                </div>
            </div>
        </div>
    );
}

'use client';

import { useSession } from 'next-auth/react';
import { useRouter } from 'next/navigation';
import { useState } from 'react';
import Script from 'next/script';
import Disclaimer from '@/components/Disclaimer';

const PLANS = [
    {
        id: 'monthly',
        name: 'Monthly',
        price: '₹999',
        period: '/month',
        features: [
            'Access to all trading signals',
            'Real-time signal notifications',
            'Full track record & history',
            'Entry, target & stop loss levels',
        ],
        featured: false,
    },
    {
        id: 'quarterly',
        name: 'Quarterly',
        price: '₹2,499',
        period: '/3 months',
        features: [
            'All Monthly plan features',
            'Save ₹498 vs monthly',
            'Priority support',
            'Detailed analysis & notes',
        ],
        featured: true,
    },
    {
        id: 'yearly',
        name: 'Yearly',
        price: '₹8,999',
        period: '/year',
        features: [
            'All Quarterly plan features',
            'Save ₹2,989 vs monthly',
            'Best value for money',
            'Exclusive market insights',
        ],
        featured: false,
    },
];

declare global {
    interface Window {
        Razorpay: any;
    }
}

export default function PricingPage() {
    const { data: session, update } = useSession();
    const router = useRouter();
    const [loading, setLoading] = useState<string | null>(null);
    const [error, setError] = useState('');

    const handlePayment = async (planId: string) => {
        if (!session) {
            router.push('/login');
            return;
        }

        setLoading(planId);
        setError('');

        try {
            // Create order
            const res = await fetch('/api/payment/create-order', {
                method: 'POST',
                headers: { 'Content-Type': 'application/json' },
                body: JSON.stringify({ planId }),
            });

            const data = await res.json();

            if (!res.ok) {
                setError(data.error || 'Failed to create order');
                setLoading(null);
                return;
            }

            // Open Razorpay Checkout
            const options = {
                key: data.keyId,
                amount: data.amount,
                currency: data.currency,
                name: 'UV Market School',
                description: `${planId.charAt(0).toUpperCase() + planId.slice(1)} Subscription`,
                order_id: data.orderId,
                handler: async function (response: any) {
                    // Verify payment
                    const verifyRes = await fetch('/api/payment/verify', {
                        method: 'POST',
                        headers: { 'Content-Type': 'application/json' },
                        body: JSON.stringify({
                            razorpay_order_id: response.razorpay_order_id,
                            razorpay_payment_id: response.razorpay_payment_id,
                            razorpay_signature: response.razorpay_signature,
                        }),
                    });

                    if (verifyRes.ok) {
                        await update({ subscriptionStatus: 'active' });
                        router.push('/dashboard');
                        router.refresh();
                    } else {
                        setError('Payment verification failed. Contact support.');
                    }
                },
                prefill: {
                    name: session.user?.name,
                    email: session.user?.email,
                },
                theme: {
                    color: '#7c3aed',
                },
            };

            const razorpay = new window.Razorpay(options);
            razorpay.on('payment.failed', function () {
                setError('Payment failed. Please try again.');
            });
            razorpay.open();
        } catch {
            setError('Something went wrong. Please try again.');
        } finally {
            setLoading(null);
        }
    };

    return (
        <>
            <Script src="https://checkout.razorpay.com/v1/checkout.js" strategy="lazyOnload" />

            <div className="page-wrapper">
                <section className="section">
                    <h1 className="section-title" style={{ fontSize: '2rem', marginBottom: '8px' }}>
                        Choose Your Plan
                    </h1>
                    <p className="text-center text-muted mb-6" style={{ fontSize: '1rem' }}>
                        Get access to all professional trading signals
                    </p>

                    {error && (
                        <div className="alert alert-error" style={{ maxWidth: '600px', margin: '0 auto 24px' }}>
                            {error}
                        </div>
                    )}

                    <div className="pricing-grid">
                        {PLANS.map((plan) => (
                            <div
                                key={plan.id}
                                className={`pricing-card ${plan.featured ? 'featured' : ''}`}
                            >
                                <div className="pricing-plan-name">{plan.name}</div>
                                <div className="pricing-amount">{plan.price}</div>
                                <div className="pricing-period">{plan.period}</div>
                                <ul className="pricing-features">
                                    {plan.features.map((f, i) => (
                                        <li key={i}>{f}</li>
                                    ))}
                                </ul>
                                <button
                                    className={`btn ${plan.featured ? 'btn-primary' : 'btn-outline'} btn-block`}
                                    onClick={() => handlePayment(plan.id)}
                                    disabled={loading === plan.id}
                                >
                                    {loading === plan.id ? 'Processing...' : 'Subscribe Now'}
                                </button>
                            </div>
                        ))}
                    </div>
                </section>

                <Disclaimer />
            </div>
        </>
    );
}

'use client';

import { useState, useEffect, useRef } from 'react';
import { useSession } from 'next-auth/react';
import { MessageCircle, X, Send, Bot, User, Loader2 } from 'lucide-react';

interface Message {
    id: string;
    message: string;
    sender: string;
    createdAt: string;
}

export default function Chatbot() {
    const { data: session } = useSession();
    const [mounted, setMounted] = useState(false);
    const [isOpen, setIsOpen] = useState(false);
    const [messages, setMessages] = useState<Message[]>([]);
    const [input, setInput] = useState('');
    const [loading, setLoading] = useState(false);
    const messagesEndRef = useRef<HTMLDivElement>(null);

    useEffect(() => {
        setMounted(true);
    }, []);

    useEffect(() => {
        if (isOpen && messages.length === 0) {
            fetchMessages();
        }
    }, [isOpen]);

    useEffect(() => {
        messagesEndRef.current?.scrollIntoView({ behavior: 'smooth' });
    }, [messages]);

    const fetchMessages = async () => {
        try {
            const res = await fetch('/api/chat');
            if (res.ok) {
                const data = await res.json();
                setMessages(data.messages || []);
            }
        } catch { }
    };

    const sendMessage = async () => {
        if (!input.trim() || loading) return;
        const text = input.trim();
        setInput('');
        setLoading(true);

        // Optimistic update
        const tempId = `temp-${Date.now()}`;
        setMessages(prev => [...prev, { id: tempId, message: text, sender: 'user', createdAt: new Date().toISOString() }]);

        try {
            const res = await fetch('/api/chat', {
                method: 'POST',
                headers: { 'Content-Type': 'application/json' },
                body: JSON.stringify({ message: text }),
            });
            const data = await res.json();
            setMessages(prev => {
                const filtered = prev.filter(m => m.id !== tempId);
                return [...filtered, data.userMessage, data.botReply];
            });
        } catch {
            setMessages(prev => prev.filter(m => m.id !== tempId));
        } finally {
            setLoading(false);
        }
    };

    if (!mounted || !session) return null;

    return (
        <>
            {/* Floating Button */}
            <button
                onClick={() => setIsOpen(!isOpen)}
                className="fixed bottom-6 right-6 z-50 w-14 h-14 bg-gradient-to-br from-purple-600 to-indigo-600 text-white rounded-full shadow-lg hover:shadow-xl transition-all flex items-center justify-center hover:scale-105 active:scale-95"
                id="chatbot-toggle"
            >
                {isOpen ? <X size={22} /> : <MessageCircle size={22} />}
            </button>

            {/* Chat Window */}
            {isOpen && (
                <div className="fixed bottom-24 right-6 z-50 w-[360px] max-h-[500px] bg-white rounded-2xl shadow-2xl border border-gray-100 flex flex-col overflow-hidden animate-in slide-in-from-bottom-5 duration-300">
                    {/* Header */}
                    <div className="bg-gradient-to-r from-purple-600 to-indigo-600 text-white p-4 flex items-center gap-3">
                        <div className="p-2 bg-white/20 rounded-full">
                            <Bot size={18} />
                        </div>
                        <div>
                            <h3 className="font-bold text-sm">UV Support Bot</h3>
                            <p className="text-xs text-purple-100">Ask anything about our services</p>
                        </div>
                    </div>

                    {/* Messages */}
                    <div className="flex-1 overflow-y-auto p-4 space-y-3 max-h-[320px] min-h-[200px] bg-gray-50/50">
                        {messages.length === 0 && (
                            <div className="text-center text-gray-400 text-sm py-8">
                                <Bot size={32} className="mx-auto mb-2 text-gray-300" />
                                <p>Say hello to get started! 👋</p>
                            </div>
                        )}
                        {messages.map((msg) => (
                            <div key={msg.id} className={`flex ${msg.sender === 'user' ? 'justify-end' : 'justify-start'}`}>
                                <div className={`max-w-[80%] px-3 py-2 rounded-xl text-sm leading-relaxed ${msg.sender === 'user'
                                    ? 'bg-purple-600 text-white rounded-br-sm'
                                    : 'bg-white text-gray-700 shadow-sm border border-gray-100 rounded-bl-sm'
                                    }`}>
                                    {msg.message}
                                </div>
                            </div>
                        ))}
                        {loading && (
                            <div className="flex justify-start">
                                <div className="bg-white text-gray-400 shadow-sm border border-gray-100 px-4 py-2 rounded-xl rounded-bl-sm text-sm flex items-center gap-2">
                                    <Loader2 size={14} className="animate-spin" /> Typing...
                                </div>
                            </div>
                        )}
                        <div ref={messagesEndRef} />
                    </div>

                    {/* Input */}
                    <div className="p-3 border-t border-gray-100 bg-white">
                        <div className="flex items-center gap-2">
                            <input
                                type="text"
                                value={input}
                                onChange={(e) => setInput(e.target.value)}
                                onKeyDown={(e) => e.key === 'Enter' && sendMessage()}
                                placeholder="Type your message..."
                                className="flex-1 px-3 py-2 rounded-lg border border-gray-200 text-sm focus:ring-2 focus:ring-purple-500/20 focus:border-purple-500 outline-none"
                            />
                            <button
                                onClick={sendMessage}
                                disabled={!input.trim() || loading}
                                className="p-2 bg-purple-600 text-white rounded-lg hover:bg-purple-700 transition-colors disabled:opacity-40"
                            >
                                <Send size={16} />
                            </button>
                        </div>
                    </div>
                </div>
            )}
        </>
    );
}

'use client';

